import React, { useEffect, useState } from 'react';
import { View, Text, Image, ScrollView, StyleSheet, ActivityIndicator } from 'react-native';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { globalStyles } from '../Style/globalFont';

export function ProfileScreen({ navigation }) {
  const [profile, setProfile] = useState(null);
  const [articles, setArticles] = useState([]);
  const [trades, setTrades] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const token = await AsyncStorage.getItem('userToken');
        if (token) {
          const response = await axios.get('http://localhost:5001/api/users/profile', {
            headers: { Authorization: `Bearer ${token}` },
          });
          setProfile(response.data);
          // Récupérez les articles et trocs si nécessaire
        } else {
          console.error('No token found');
        }
      } catch (error) {
        console.error('Erreur lors de la récupération des données :', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchProfileData();
  }, []);

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#A0AE88',
    },
    header: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 20,
      paddingTop: 50,
      paddingBottom: 20,
      borderBottomWidth: 1,
      borderBottomColor: '#ccc',
    },
    title: {
      fontFamily: 'Rowdies',
      fontSize: 24,
      fontWeight: 'bold',
      color: 'white',
    },
    scrollContent: {
      flexGrow: 1,
      alignItems: 'center',
      paddingBottom: 80,
    },
    profileInfo: {
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: '#fff',
      padding: 20,
      borderRadius: 10,
      marginBottom: 20,
      width: '90%',
    },
    profilePic: {
      width: 80,
      height: 80,
      borderRadius: 40,
      marginRight: 20,
    },
    profileDetails: {
      flex: 1,
    },
    name: {
      fontFamily: 'Rowdies',
      fontSize: 18,
      fontWeight: 'bold',
    },
    city: {
      fontFamily: 'Rowdies',
      fontSize: 16,
    },
    articlesGrid: {
      flexDirection: 'row',
      flexWrap: 'wrap',
      justifyContent: 'space-between',
      width: '90%',
    },
    articleCard: {
      backgroundColor: '#C9AC96',
      width: '31%',
      aspectRatio: 1,
      margin: 5,
      borderRadius: 10,
    },
  });

  if (isLoading) {
    return <ActivityIndicator size="large" color="#0000ff" />;
  }

  if (!profile) {
    return (
      <View style={styles.container}>
        <Text>Profil non disponible</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.profileInfo}>
          {profile.profileImage ? (
            <Image source={{ uri: profile.profileImage }} style={styles.profilePic} />
          ) : (
            <Image source={require('../assets/profile-pic.jpg')} style={styles.profilePic} />
          )}
          <View style={styles.profileDetails}>
            <Text style={styles.name}>{profile.name}</Text>
            <Text style={styles.city}>{profile.city}</Text>
            <Text style={styles.email}>{profile.email}</Text>
          </View>
        </View>
        {/* Affichage des articles et trocs */}
        {/* ... */}
      </ScrollView>
    </View>
  );
}
