import React, { useEffect, useState } from 'react';
import { View, Text, Image,Pressable, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import axios from 'axios';
import { Ionicons } from '@expo/vector-icons'; 

export function ProfileScreen({ route, navigation}) {

  const [profile, setProfile] = useState(null);
  const [articles, setArticles] = useState([]);
  const [trades, setTrades] = useState([]); // Pour stocker les trocs
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const token = await AsyncStorage.getItem('userToken');
        const response = await axios.get('http://localhost:5001/api/users/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(response.data);

        // Récupération des articles associés au profil
        const articlesResponse = await axios.get(`http://localhost:5001/api/articles/user/${response.data.id}`);
        setArticles(articlesResponse.data);

        // Récupération des trocs passés de l'utilisateur (exemple)
        const tradesResponse = await axios.get(`http://localhost:5001/api/trades/user/${response.data.id}`);
        setTrades(tradesResponse.data);
      } catch (error) {
        console.error('Erreur lors de la récupération des données :', error);
      }
    };

    fetchProfileData();
  }, []);



//EDIT MY ACCOUNT
  const handleEditProfile = () => {
    setIsEditing(true); 
    // Logique de navigation vers l'écran d'édition du profil
    navigation.navigate('EditProfile', { profile }); // Passer les données du profil à l'écran d'édition
  };


  //DELETE MY ACCOUNT
  const handleDeleteProfile = () => {
    Alert.alert(
      'Suppression du profil',
      'Êtes-vous sûr de vouloir supprimer votre profil ? ',
      [
        { text: 'Annuler', style: 'cancel' },
        {
          text: 'Supprimer',
          style: 'destructive',
          onPress: async () => {
            try {
              const response = await axios.delete('http://localhost:5001/api/users/profile', {
                headers: { Authorization: `Bearer ${route.params.token}` },
              });
              console.log(response.data);
              navigation.navigate('SignInScreen'); // Rediriger vers la page de connexion après la suppression
            } catch (error) {
              console.error('Erreur lors de la suppression du profil :', error);
              // Gestion des erreurs (afficher un message d'erreur à l'utilisateur)
            }
          },
        },
      ]
    );
  };

  return (
<View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.title}>Mon Profil</Text>
        <View style={styles.icons}>
          {!isEditing && ( 
            <TouchableOpacity onPress={handleEditProfile}>
              <Ionicons name="create-outline" size={24} color="black" />
            </TouchableOpacity>
          )}
          <TouchableOpacity onPress={handleDeleteProfile}>
            <Ionicons name="trash-outline" size={24} color="red" />
          </TouchableOpacity>
        </View>
      </View>

      <ScrollView contentContainerStyle={styles.scrollContent}>
        {/* Informations du profil */}
       {/* Informations du profil */}
       <View style={styles.profileInfo}>
          {/* ... */}
          <View style={styles.profileDetails}>
            <Text style={styles.name}>{profile.name}</Text>
            <Text style={styles.city}>{profile.city}</Text>
            <Text style={styles.email}>{profile.email}</Text>
          </View>
        </View>

        {/* Articles */}
        <View style={styles.articlesGrid}>
          {articles.map((article, index) => (
            <ArticlePreview key={index} article={article} />
          ))}
        </View>

        {/* Trocs passés */}
        <PastTrades trades={trades} />
      </ScrollView>

      {/* Barre de navigation inférieure */}
      <View style={styles.bottomNavBar}>
      <Pressable style={styles.iconButton} onPress={() => navigation.navigate('Welcome')}>
          <Ionicons name="home-outline" size={24} color="black" />
        </Pressable>
        <Pressable style={styles.iconButton} onPress={() => navigation.navigate('ProfileScreen', { name, city, email })}>
          <Ionicons name="person-outline" size={24} color="black" />
        </Pressable>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#A0AE88', // Couleur de fond principale
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingTop: 50, // Marge supérieure plus importante
    paddingBottom: 20,
    borderBottomWidth: 1, // Ligne de séparation
    borderBottomColor: '#ccc', // Couleur de la ligne de séparation
  },
  title: {
    fontFamily: 'Rowdies',
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
  },
  icons: {
    flexDirection: 'row',
  },
  scrollContent: {
    flexGrow: 1, // Permet au contenu de remplir l'espace disponible
    alignItems: 'center',
    paddingBottom: 80, // Espace pour la barre de navigation inférieure
  },
  profileInfo: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 20,
    borderRadius: 10,
    marginBottom: 20,
    width: '90%', // Largeur du conteneur d'informations du profil
  },
  profilePic: {
    width: 80,
    height: 80,
    borderRadius: 40,
    marginRight: 20,
  },
  profileDetails: {
    flex: 1, // Permet aux détails de prendre l'espace restant
  },
  name: {
    fontFamily: 'Rowdies',
    fontSize: 18,
    fontWeight: 'bold',
  },
  city: {
    fontFamily: 'Rowdies',
    fontSize: 16,
  },
  stats: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 10,
  },
  statItem: {
    alignItems: 'center',
  },
  statNumber: {
    fontFamily: 'Rowdies',
    fontSize: 16,
    fontWeight: 'bold',
  },
  statLabel: {
    fontFamily: 'Rowdies',
    fontSize: 14,
  },
  articlesGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    width: '90%', // Largeur de la grille d'articles
  },
  articleCard: {
    backgroundColor: '#C9AC96', 
    width: '31%', // Largeur d'une carte (3 colonnes avec une petite marge)
    aspectRatio: 1, // Maintient le ratio 1:1 (carré)
    margin: 5,
    borderRadius: 10,
  },
  bottomNavBar: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    backgroundColor: '#D9D9D9',
    width: '100%',
    padding: 15,
    position: 'absolute',
    bottom: 0,
  },
});
