import React, { useState, useEffect } from 'react';
import * as Font from 'expo-font';
import { StyleSheet, Text } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { WelcomeScreen } from './components/WelcomScreen';
import { SignInScreen } from './components/SignInScreen';
import { SignUpScreen } from './components/SignUpScreen';
import { ProfileScreen } from './components/ProfileScreen';
import { ConfirmationScreen } from './components/ConfirmationScreen';



const Stack = createStackNavigator();

const styles = StyleSheet.create({
  defaultText: {
    fontFamily: 'Rowdies',
  },
});
export default function App() {
  const [profile, setProfile] = useState(null);
  // const [articles, setArticles] = useState([]);
  // const [trades, setTrades] = useState([]); // Pour stocker les trocs


  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const token = await AsyncStorage.getItem('userToken');
        const response = await axios.get('http://localhost:5001/api/users/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(response.data);

        // Récupération des articles associés au profil
        // const articlesResponse = await axios.get(`http://localhost:5001/api/articles/user/${response.data.id}`);
        // setArticles(articlesResponse.data);

        // // Récupération des trocs passés de l'utilisateur (exemple)
        // const tradesResponse = await axios.get(`http://localhost:5001/api/trades/user/${response.data.id}`);
        // setTrades(tradesResponse.data);
      } catch (error) {
        console.error('Erreur lors de la récupération des données :', error);
      }
    };

    fetchProfileData();
  }, []);

  const [fontsLoaded, setFontsLoaded] = useState(false);

  useEffect(() => {
    async function loadFonts() {
      await Font.loadAsync({
        'Rowdies': require('./assets/fonts/Rowdies-Regular.ttf'), // Chemin vers votre fichier de police
      });
      setFontsLoaded(true);
    }

    loadFonts();
  }, []);

  if (!fontsLoaded) {
    return null; 
  }




  return (

    <NavigationContainer screenOptions={{
      headerShown: false, // Masquer l'en-tête par défaut de la navigation (optionnel)
      contentStyle: styles.defaultText // Appliquer le style par défaut au contenu des écrans
    }}>

      <Stack.Navigator >
        <Stack.Screen name="Welcome" component={WelcomeScreen} />
        <Stack.Screen name="SignInScreen"  component={SignInScreen} />
        <Stack.Screen name="SignUpScreen" component={SignUpScreen} />
        <Stack.Screen  name="ProfileScreen" profile={profile} articles={articles} trades={trades} component={ProfileScreen}  /> 
        <Stack.Screen name="Confirmation" component={ConfirmationScreen} />

      </Stack.Navigator>


    </NavigationContainer>

  );







}
