import React, { useEffect, useState } from 'react';
import { View, Text, Image, Pressable, StyleSheet, ScrollView, TouchableOpacity, Alert, ActivityIndicator } from 'react-native';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { globalStyles } from '../Style/globalFont';
import { Ionicons } from '@expo/vector-icons'; 
import { EditProfilePicture } from './EditProfilePicture';

export function ProfileScreen({ route, navigation }) {
  const [profile, setProfile] = useState(null);
  const [articles, setArticles] = useState([]);
  const [trades, setTrades] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const token = await AsyncStorage.getItem('userToken');
        if (token) {
          const response = await axios.get('http://localhost:5001/api/users/profile', {
            headers: { Authorization: `Bearer ${token}` },
          });
          setProfile(response.data);
          // Récupération des articles et trocs (non montré ici)
        }
      } catch (error) {
        console.error('Erreur lors de la récupération des données :', error);
        // Gérer l'erreur ici
      } finally {
        setIsLoading(false);
      }
    };

    fetchProfileData();
  }, []);

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#A0AE88',
    },
    header: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 20,
      paddingTop: 50,
      paddingBottom: 20,
      borderBottomWidth: 1,
      borderBottomColor: '#ccc',
    },
    title: {
      fontFamily: 'Rowdies',
      fontSize: 24,
      fontWeight: 'bold',
      color: 'white',
    },
    icons: {
      flexDirection: 'row',
    },
    scrollContent: {
      flexGrow: 1,
      alignItems: 'center',
      paddingBottom: 80,
    },
    profileInfo: {
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: '#fff',
      padding: 20,
      borderRadius: 10,
      marginBottom: 20,
      width: '90%',
    },
    profilePic: {
      width: 80,
      height: 80,
      borderRadius: 40,
      marginRight: 20,
    },
    profileDetails: {
      flex: 1,
    },
    name: {
      fontFamily: 'Rowdies',
      fontSize: 18,
      fontWeight: 'bold',
    },
    city: {
      fontFamily: 'Rowdies',
      fontSize: 16,
    },
    stats: {
      flexDirection: 'row',
      justifyContent: 'space-around',
      marginTop: 10,
    },
    statItem: {
      alignItems: 'center',
    },
    statNumber: {
      fontFamily: 'Rowdies',
      fontSize: 16,
      fontWeight: 'bold',
    },
    statLabel: {
      fontFamily: 'Rowdies',
      fontSize: 14,
    },
    articlesGrid: {
      flexDirection: 'row',
      flexWrap: 'wrap',
      justifyContent: 'space-between',
      width: '90%',
    },
    articleCard: {
      backgroundColor: '#C9AC96',
      width: '31%',
      aspectRatio: 1,
      margin: 5,
      borderRadius: 10,
    },
    bottomNavBar: {
      flexDirection: 'row',
      justifyContent: 'space-around',
      alignItems: 'center',
      backgroundColor: '#D9D9D9',
      width: '100%',
      padding: 15,
      position: 'absolute',
      bottom: 0,
    },
    loading: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
    },
  });

  if (isLoading) {
    return (
      <View style={styles.loading}>
        <ActivityIndicator size="large" color="#6E745C" />
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        {profile && (
          <View style={styles.profileInfo}>
            {profile.profileImage ? (
              <Image source={{ uri: profile.profileImage }} style={styles.profilePic} />
            ) : (
              <Image source={require('./assets/profile-pic.jpg')} style={styles.profilePic} />
            )}
            <View style={styles.profileDetails}>
              <Text style={styles.name}>{profile.name}</Text>
              <Text style={styles.city}>{profile.city}</Text>
              <Text style={styles.email}>{profile.email}</Text>
            </View>
          </View>
        )}

        {articles.length > 0 && (
          <View style={styles.articlesGrid}>
            {articles.map((article, index) => (
              <View key={index} style={styles.articleCard}>
                <Text>{article.title}</Text>
              </View>
            ))}
          </View>
        )}

        {trades.length > 0 && (
          <View>
            <Text>Trocs passés:</Text>
            {trades.map((trade, index) => (
              <View key={index}>
                <Text>{trade.details}</Text>
              </View>
            ))}
          </View>
        )}
      </ScrollView>
    </View>
  );
}
