import React, { useState, useEffect } from 'react';
import { View, Text, Image, Button, StyleSheet } from 'react-native';
import axios from 'axios';

export function ProfileScreen() {
  const [profileData, setProfileData] = useState(null);

  useEffect(() => {
    // Fonction pour récupérer les données du profil depuis le backend
    const fetchProfileData = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/users/');
        setProfileData(response.data);
      } catch (error) {
        console.error('Erreur lors de la récupération des données du profil :', error);
      }
    };

    fetchProfileData();
  }, []);

  if (!profileData) {
    return <Text>Chargement...</Text>;
  }

  return (
    <View style={styles.container}>
      <Image source={require('./assets/profile-pic.jpg')} style={styles.profilePic} />
      <Text style={styles.name}>{profileData.name}</Text>
      <Text style={styles.city}>{profileData.city}</Text>
      <Text style={styles.email}>{profileData.email}</Text>
      <Button title="Modifier le profil" onPress={() => {/* Fonction pour modifier le profil */}} />
      <Button title="Déconnexion" onPress={() => {/* Fonction pour se déconnecter */}} />
      <Button title="Supprimer mon profil" onPress={() => {/* Fonction pour supprimer mon profil */}} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  profilePic: {
    width: 100,
    height: 100,
    borderRadius: 50,
    marginBottom: 20,
  },
  name: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  city: {
    fontSize: 20,
    marginBottom: 10,
  },
  email: {
    fontSize: 18,
    marginBottom: 20,
  },
});
