import "../node_modules/@openfonts/rowdies_all"
import React ,{Component}from 'react';
import { StyleSheet,Image, View, Text, Pressable } from 'react-native';
import { StatusBar } from "expo-status-bar";

//Contient le logo
const PlaceholderLogo = require("../assets/logo.png");
//Contient le 1er text de bienvenue
const FirstTitleApp = " Bienvenue sur Trocante";
const HomeText2 = `L’appli Qui va changer ta manière de troquer 
Ecolo et gratuite.`;
export class WelcomeScreen extends Component  {


  render() {
    return (
      <View  style={styles.container}>
        <View style={styles.image}>  
        <Image source={PlaceholderLogo} style={styles.image}
        /></View>

        <StatusBar style="auto" />
        <Text style={styles.card1Tittle}>{ FirstTitleApp}
     </Text>
     <View style={styles.contentText} >
      <Text style={styles.text1}>{HomeText2}</Text>
      </View>

      <Pressable style={styles.buttonPush }  onPress={() => navigation.navigate('SignInScreen')}>
      <Text>Se connecter</Text> 
      </Pressable>
      <Pressable style={styles.buttonPush }  onPress={() => navigation.navigate('SignUpScreen')}>
      <Text>S'inscrire</Text> 
      </Pressable>

      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {

    backgroundColor: "#A0AE88",
    alignItems: "center",
    justifyContent: "center",
    gap:"5%",
    width:"100%",
    height:"100%",
  },

  image:{
    width:100,
    height:100,
    borderRadius:30
  },
  card1Tittle:{
    borderBlockColor:"#D9D9D9",
    display:"flex",
    justifyContent:"center",
    backgroundColor:"#D9D9D9",
    padding:"5%",
    textAlign: "center",
    width:"50%",
    borderRadius:20,
    fontSize: 16,
    fontFamily: "rowdies",
    fontWeight: "bold",
    color: "#FFFFFF",

  },
  text: {
    fontSize: 16,
    fontFamily: "rowdies",
    fontWeight: "bold",
    color: "#FFFFFF",
  },
  contentText:{ 
    borderRadius:"15px",
    // paddingTop:"30%",
    // width:"300px",
    backgroundColor:"#6E745C",

  },
  text1:{
    margin: "2%",
    height: "225px",
    alignItems: "center",
    textAlign: "center",
    paddingTop: "30%",
    fontSize: 14,
    fontFamily:"rowdies",
    fontWeight:"light",
    color:"#FFFFFF"
  },
  buttonPush:{
    backgroundColor:"#D9D9D9",
    color:"black",
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 22,
    paddingHorizontal: 92,
    borderRadius: 10,
  },

});


