//import "../node_modules/@openfonts/rowdies_all"
import React from 'react';
import { StyleSheet,Image, View, Text, Pressable } from 'react-native';
import { StatusBar } from "expo-status-bar";
import { useNavigation } from '@react-navigation/native'; 
import * as Font from 'expo-font'; // Import de expo-font


//Contient le logo
const PlaceholderLogo = require("../assets/logo.png");
//Contient le 1er text de bienvenue
const FirstTitleApp = " Bienvenue sur Trocante";
const HomeText2 = `L’appli Qui va changer ta manière de troquer 
Ecolo et gratuite.`;





export function WelcomeScreen () {
  const navigation = useNavigation(); // Accès à l'objet navigation

  const onPressConnexion = () => {
    navigation.navigate('SignInScreen');
  };

  const onPressInscription = () => {
    navigation.navigate('SignUpScreen');

  };

  const [fontsLoaded, setFontsLoaded] = useState(false);


  useEffect(() => {
    async function loadFonts() {
      await Font.loadAsync({
        'Rowdies': require('../assets/fonts/Rowdies-Regular.ttf'), // Chemin vers votre fichier de police Rowdies
      });
      setFontsLoaded(true);
    }

    loadFonts();
  }, []);

  if (!fontsLoaded) {
    return null; // Ou un composant de chargement pendant que la police est chargée
  }

  return (
    <View style={styles.container}> 
      <StatusBar style="auto" />

      <Image source={PlaceholderLogo} style={styles.logo} />

      <View style={styles.welcomeContainer}>
        <Text style={styles.welcomeTitle}>{FirstTitleApp}</Text>
        <Text style={styles.welcomeText}>{HomeText2}</Text>
      </View>

      <Pressable style={styles.button} onPress={onPressConnexion}>
        <Text style={styles.buttonText}>Se connecter</Text>
      </Pressable>
      <Pressable style={styles.button} onPress={onPressInscription}>
        <Text style={styles.buttonText}>S'inscrire</Text>
      </Pressable>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#A0AE88",
    alignItems: "center",
    justifyContent: "center",
    paddingHorizontal: 20, // Ajoute un padding pour un meilleur équilibre visuel
  },
  logo: {
    width: 100,
    height: 100,
    borderRadius: 50, // Rend le logo circulaire
    marginBottom: 30,  
  },
  welcomeContainer: {
    backgroundColor: "#D9D9D9",
    padding: 20,
    borderRadius: 15,
    marginBottom: 20,
    alignItems: 'center', // Centre le texte dans le conteneur
  },
  welcomeTitle: {
    fontFamily: 'Rowdies',
    fontSize: 18,
    fontWeight: "bold",
    color: "#FFFFFF",
    marginBottom: 10,
  },
  welcomeText: {
    fontFamily: 'Rowdies',
    fontSize: 16,
    textAlign: "center",
    color: "#6E745C",
  },
  button: {
    backgroundColor: "#D9D9D9",
    paddingVertical: 15,
    paddingHorizontal: 40,
    borderRadius: 10,
    marginVertical: 5,  
  },
  buttonText: {
    fontFamily: 'Rowdies',
    color: "#6E745C",
    fontWeight: "bold",
    fontSize: 16,
  },
});
