//Contient le logo
const PlaceholderLogo = require("../assets/logo.png");
//Contient le 1er text de bienvenue
const FirstTitleApp = " Bienvenue sur Trocante";
const HomeText2 = `L’appli Qui va changer ta manière de troquer 
Ecolo et gratuite.`;
export class WelcomeScreen  {


  render() {
    return (
      <View class="content-center" style={styles.container}>
        <View style={styles.imageContainer}>  
        <Image source={PlaceholderLogo} style={styles.image}
        /></View>

        <StatusBar style="auto" />
        <Text style={styles.card1Tittle}>{ FirstTitleApp}
     </Text>
      <Text style={styles.text1}>{HomeText2}</Text>

      <Pressable style={styles.buttonPush }
        title="Se connecter"
        onPress={() => navigation.navigate('SignIn')}
      />
      <Pressable style={styles.buttonPush }
        title="S'inscrire"
        onPress={() => navigation.navigate('SignUp')}
      />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#A0AE88",
    alignItems: "center",
    justifyContent: "center",
    gap:"10%",
    width:"100%",
  },
  imageContainer: {
    flex: 1,
    paddingTop: 58,
  },
  image:{
    width:150,
    height:150,
    borderRadius:30
  },
  card1Tittle:{
    borderBlockColor:"#D9D9D9",
    display:"flex",
    justifyContent:"center",
    backgroundColor:"#D9D9D9",
    padding:"5%",
    width:"50%",
    borderRadius:20,
    fontSize: 16,
    fontFamily: "rowdies",
    fontWeight: "bold",
    color: "#FFFFFF",

  },
  text: {
    fontSize: 16,
    fontFamily: "rowdies",
    fontWeight: "bold",
    color: "#FFFFFF",
  },
  text1:{
    borderRadius:"15px",
    paddingTop:"30%",
    fontSize: 14,
    fontFamily:"rowdies",
    backgroundColor:"#6E745C",
    width:"300px",
    fontWeight:"light",
    color:"#FFFFFF"
  },
  buttonPush:{
    backgroundColor:"#D9D9D9",
    //gap:"10%",
    color:"black",
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 22,
    paddingHorizontal: 92,
    borderRadius: 10,


  },

