import React, { useEffect, useState } from 'react';
import { View, Text, Image, StyleSheet, ScrollView, ActivityIndicator } from 'react-native';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { ArticlePreview } from './ArticlePreview';
import { PastTrades } from './PastTrades';
import { globalStyles } from '../Style/globalFont';

export function ProfileScreen({ route, navigation }) {
  const [profile, setProfile] = useState(null);
  const [articles, setArticles] = useState([]);
  const [trades, setTrades] = useState([]);
  const [isLoading, setIsLoading] = useState(true);


          // Récupérer les articles
          // const articlesResponse = await axios.get(`http://localhost:5001/api/articles/user/${response.data.id}`);
          // setArticles(articlesResponse.data);

          // // Récupérer les trocs
          // const tradesResponse = await axios.get(`http://localhost:5001/api/trades/user/${response.data.id}`);
          // setTrades(tradesResponse.data);



  useEffect(() => {
    const fetchProfileData = async () => {
      setIsLoading(true);
      try {
        const token = await AsyncStorage.getItem('userToken');
        if (token) {
          console.log('Token found:', token); // Log pour vérifier le token
          const response = await axios.get('http://localhost:5001/api/users/profile', {
            headers: { Authorization: `Bearer ${token}` },
          });
          console.log('Profile data:', response.data); // Log pour vérifier les données du profil
          setProfile(response.data);

        } else {
          console.log('No token found');
        }
      } catch (error) {
        console.error('Erreur lors de la récupération des données :', error);

      }finally {
         setIsLoading(false);
       }
    };

    fetchProfileData();
  }, []);

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#A0AE88',
    },
    loading: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
    },
    scrollContent: {
      flexGrow: 1,
      alignItems: 'center',
      paddingBottom: 80,
    },
    profileInfo: {
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: '#fff',
      padding: 20,
      borderRadius: 10,
      marginBottom: 20,
      width: '90%',
    },
    profilePic: {
      width: 80,
      height: 80,
      borderRadius: 40,
      marginRight: 20,
    },
    profileDetails: {
      flex: 1,
    },
    name: {
      fontFamily: 'Rowdies',
      fontSize: 18,
      fontWeight: 'bold',
    },
    city: {
      fontFamily: 'Rowdies',
      fontSize: 16,
    },
    articlesGrid: {
      flexDirection: 'row',
      flexWrap: 'wrap',
      justifyContent: 'space-between',
      width: '90%',
    },
    articleCard: {
      backgroundColor: '#C9AC96',
      width: '31%',
      aspectRatio: 1,
      margin: 5,
      borderRadius: 10,
      alignItems: 'center',
      justifyContent: 'center',
    },
  });

  if (isLoading) {
    return (
      <View style={styles.loading}>
        <ActivityIndicator size="large" color="#6E745C" />
      </View>
    );
  }

  return (

    <View style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContent}>

      <View style={styles.profileInfo}>
  {profile ? ( // <-- Vérifier si profile existe
    <>
      <Image
        source={profile.profileImage ? { uri: profile.profileImage } : require('./assets/profile-pic.jpg')}
        style={styles.profilePic}
      />
      <View style={styles.profileDetails}>
        <Text style={styles.name}>{profile.name}</Text>
        <Text style={styles.city}>{profile.city}</Text>
        <Text style={styles.email}>{profile.email}</Text>
      </View>
    </>
  ) : (
    <ActivityIndicator size="large" color="#0000ff" /> // Afficher un indicateur de chargement
  )}
    </View>

        {/* {articles.length > 0 ? (
          <View style={styles.articlesGrid}>
            {articles.map((article, index) => (
              <View key={index} style={styles.articleCard}>
                <Text>{article.title}</Text>
              </View>
            ))}
          </View>
        ) : (
          <Text>Aucun article disponible</Text>
        )}

        {trades.length > 0 ? (
          <View>
            <Text>Trocs passés:</Text>
            {trades.map((trade, index) => (
              <View key={index}>
                <Text>{trade.details}</Text>
              </View>
            ))}
          </View>
        ) : (
          <Text>Aucun troc passé disponible</Text>
        )} */}
      </ScrollView>
    </View>
  );
}
