class WelcomeScreen extends Component {
  render() {
    return (
      <View style={styles.container}>

        <Image style={{width:"100%"}}
          source={require("../assets/logo.png")}
          resizeMode="contain"
        />
        <Text style={styles.card1Tittle}>
      Bienvenue sur Trocante</Text>
      <Text style={styles.text1}>L’appli Qui va changer ta manière de troquer 
        Ecolo et gratuite.</Text>

      <Pressable style={styles.buttonPush }
        title="Se connecter"
        onPress={() => navigation.navigate('SignIn')}
      />
      <Pressable style={styles.buttonPush }
        title="S'inscrire"
        onPress={() => navigation.navigate('SignUp')}
      />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#A0AE88",
    alignItems: "center",
    justifyContent: "center",
    gap:"10%",
  },
  card1Tittle:{
    //borderBlockColor:"#D9D9D9",
    display:"flex",
    justifyContent:"center",
    backgroundColor:"#D9D9D9",
    padding:"5%",
    width:"50%",
    borderRadius:"20px",
    fontSize: 16,
    fontFamily: "rowdies",
    fontWeight: "bold",
    color: "#FFFFFF",

  },
  text: {
    fontSize: 16,
    fontFamily: "rowdies",
    fontWeight: "bold",
    color: "#FFFFFF",
  },
  text1:{
    borderRadius:"15px",
    paddingTop:"30%",
    fontSize: 14,
    fontFamily:"rowdies",
    backgroundColor:"#6E745C",
    width:"300px",
    fontWeight:"light",
    color:"#FFFFFF"
  },
  buttonPush:{
    backgroundColor:"#D9D9D9",
    gap:"10%",
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,

  },

});

export default WelcomeScreen;
