import React, { useEffect, useState } from 'react';
import { View, Text, Image, Pressable, StyleSheet, ScrollView, TouchableOpacity, Alert, ActivityIndicator } from 'react-native'; // Import ActivityIndicator
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { globalStyles } from '../Style/globalFont';
import { Ionicons } from '@expo/vector-icons'; 
import { EditProfilePicture } from './EditProfilePicture';

export function ProfileScreen({ route, navigation }) {
  const [profile, setProfile] = useState(null);
  const [articles, setArticles] = useState([]);
  const [trades, setTrades] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(true); // Nouvel état pour le chargement

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const token = await AsyncStorage.getItem('userToken');
        if (token) {
          const response = await axios.get('http://localhost:5001/api/users/profile', {
            headers: { Authorization: `Bearer ${token}` },
          });
          setProfile(response.data);

          // ... (récupération des articles et trocs, comme avant)
        }
      } catch (error) {
        console.error('Erreur lors de la récupération des données :', error);
        // Gérez l'erreur ici, par exemple en affichant un message à l'utilisateur
      } finally {
        setIsLoading(false); // Fin du chargement, que la requête réussisse ou échoue
      }
    };

    fetchProfileData();
  }, []);

  // ... (vos fonctions handleEditProfile, handleDeleteProfile)

  return (
    <View style={styles.container}>
      {/* Header */}
      {/* ... */}

      <ScrollView contentContainerStyle={styles.scrollContent}>
        {/* Informations du profil */}
        {isLoading ? ( // Affichage d'un indicateur de chargement
          <ActivityIndicator size="large" color="#6E745C" /> 
        ) : (
          <View style={styles.profileInfo}>
            {profile.profileImage ? ( 
              <Image source={{ uri: profile.profileImage }} style={styles.profilePic} />
            ) : (
              <Image source={require('./assets/profile-pic.jpg')} style={styles.profilePic} />
            )}
            <View style={styles.profileDetails}>
              <Text style={styles.name}>{profile.name}</Text>
              <Text style={styles.city}>{profile.city}</Text>
              <Text style={styles.email}>{profile.email}</Text>
            </View>
          </View>
        )}

        {/* ... (articles et trocs passés) */}
      </ScrollView>

      {/* Barre de navigation inférieure */}
      {/* ... */}
    </View>
  );
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#A0AE88', // Couleur de fond principale
    },
    header: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 20,
      paddingTop: 50, // Marge supérieure plus importante
      paddingBottom: 20,
      borderBottomWidth: 1, // Ligne de séparation
      borderBottomColor: '#ccc', // Couleur de la ligne de séparation
    },
    title: {
      fontFamily: 'Rowdies',
      fontSize: 24,
      fontWeight: 'bold',
      color: 'white',
    },
    icons: {
      flexDirection: 'row',
    },
    scrollContent: {
      flexGrow: 1, // Permet au contenu de remplir l'espace disponible
      alignItems: 'center',
      paddingBottom: 80, // Espace pour la barre de navigation inférieure
    },
    profileInfo: {
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: '#fff',
      padding: 20,
      borderRadius: 10,
      marginBottom: 20,
      width: '90%', // Largeur du conteneur d'informations du profil
    },
    profilePic: {
      width: 80,
      height: 80,
      borderRadius: 40,
      marginRight: 20,
    },
    profileDetails: {
      flex: 1, // Permet aux détails de prendre l'espace restant
    },
    name: {
      fontFamily: 'Rowdies',
      fontSize: 18,
      fontWeight: 'bold',
    },
    city: {
      fontFamily: 'Rowdies',
      fontSize: 16,
    },
    stats: {
      flexDirection: 'row',
      justifyContent: 'space-around',
      marginTop: 10,
    },
    statItem: {
      alignItems: 'center',
    },
    statNumber: {
      fontFamily: 'Rowdies',
      fontSize: 16,
      fontWeight: 'bold',
    },
    statLabel: {
      fontFamily: 'Rowdies',
      fontSize: 14,
    },
    articlesGrid: {
      flexDirection: 'row',
      flexWrap: 'wrap',
      justifyContent: 'space-between',
      width: '90%', // Largeur de la grille d'articles
    },
    articleCard: {
      backgroundColor: '#C9AC96', 
      width: '31%', // Largeur d'une carte (3 colonnes avec une petite marge)
      aspectRatio: 1, // Maintient le ratio 1:1 (carré)
      margin: 5,
      borderRadius: 10,
    },
    bottomNavBar: {
      flexDirection: 'row',
      justifyContent: 'space-around',
      alignItems: 'center',
      backgroundColor: '#D9D9D9',
      width: '100%',
      padding: 15,
      position: 'absolute',
      bottom: 0,
    },
  });
}
