import React, { useEffect, useState } from 'react';
import { View, Text, Image, Button, StyleSheet } from 'react-native';
import axios from 'axios';

export function ProfileScreen({ route, navigation}) {
  const [profile, setProfile] = useState({
    name: '',
    city: '',
    email: '',
    profileImage: '',
  });

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/users/profile', {
          headers: { Authorization: `Bearer ${route.params.token}` },
        });
        setProfile(response.data);
      } catch (error) {
        console.error('Erreur lors de la récupération des données du profil :', error);
      }
    };

    fetchProfileData();
  }, []);

  return (
    <View style={styles.container}>
      <Image source={profile.profileImage ? { uri: profile.profileImage } : require('./assets/profile-pic.jpg')} style={styles.profilePic} />
      <Text style={styles.name}>{profile.name}</Text>
      <Text style={styles.city}>{profile.city}</Text>
      <Text style={styles.email}>{profile.email}</Text>
      <Button title="Modifier le profil" onPress={() => {/* Fonction pour modifier le profil */}} />
      <Button title="Déconnexion" onPress={() => navigation.navigate('SignInScreen')} />
      <Button title="Supprimer mon profil" onPress={() => {/* Fonction pour supprimer le profil */}} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  profilePic: {
    width: 100,
    height: 100,
    borderRadius: 50,
    marginBottom: 20,
  },
  name: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  city: {
    fontSize: 18,
    marginBottom: 10,
  },
  email: {
    fontSize: 18,
    marginBottom: 20,
  },
});
