import React, { useState, useEffect } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import * as Font from 'expo-font';
import { StyleSheet, Text } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { WelcomeScreen } from './components/WelcomScreen';
import { SignInScreen } from './components/SignInScreen';
import { SignUpScreen } from './components/SignUpScreen';
import { ProfileScreen } from './components/ProfileScreen';
import { ConfirmationScreen } from './components/ConfirmationScreen';



const Stack = createStackNavigator();

const styles = StyleSheet.create({
  defaultText: {
    fontFamily: 'Rowdies',
  },
});
export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const token = await AsyncStorage.getItem('userToken'); // Ou la clé que vous utilisez
        if (token) {
          setIsLoggedIn(true);
        }
      } catch (error) {
        console.error('Erreur lors de la vérification du statut de connexion :', error);
      }
    };

    checkLoginStatus();
  }, []);

  const [fontsLoaded, setFontsLoaded] = useState(false);

  useEffect(() => {
    async function loadFonts() {
      await Font.loadAsync({
        'Rowdies': require('./assets/fonts/Rowdies-Regular.ttf'), // Chemin vers votre fichier de police
      });
      setFontsLoaded(true);
    }

    loadFonts();
  }, []);

  if (!fontsLoaded) {
    return null; // Ou un composant de chargement
  }


  return (

    <NavigationContainer screenOptions={{
      headerShown: false, // Masquer l'en-tête par défaut de la navigation (optionnel)
      contentStyle: styles.defaultText // Appliquer le style par défaut au contenu des écrans
    }}>

      <Stack.Navigator >
      {isLoggedIn ? (
          <Stack.Screen name="ProfileScreen" component={ProfileScreen} />
        ) : (
          <>
            <Stack.Screen name="Welcome" component={WelcomeScreen} options={{ headerShown: false }} />
            <Stack.Screen name="SignInScreen" component={SignInScreen} />
            <Stack.Screen name="SignUpScreen" component={SignUpScreen} />
          </>
        )}
      </Stack.Navigator>


    </NavigationContainer>

  );







}
