import { AddArticle } from './AddArticle';
import { ArticlePreview } from './ArticlePreview';
import { PastTrades } from './PastTrades';

export function ProfileScreen({ route, navigation }) {
  // ... (votre logique de récupération des données)

  return (
    <View style={styles.container}>
      {/* ... (vos autres éléments) */}

      <EditProfilePicture profileImage={profile.profileImage} onImageChange={(newImageUri) => {
        // Mettez à jour profile.profileImage avec la nouvelle URI de l'image
      }} />

      {/* ... (vos autres éléments) */}

      <AddArticle />

      <View style={styles.articlesSection}>
        <Text style={styles.sectionTitle}>Mes articles</Text>
        <FlatList
          data={articles}
          renderItem={({ item }) => <ArticlePreview article={item} />}
          keyExtractor={(item) => item.id}
          horizontal
          showsHorizontalScrollIndicator={false}
        />
      </View>

      <PastTrades trades={[]} /> {/* Remplacez [] par vos données de trocs */}
    </View>
  );
}
