import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Pressable, Image, Alert, StyleSheet } from 'react-native';
import axios from 'axios';
import * as ImagePicker from 'react-native-image-picker';
import * as Font from 'expo-font';

export const SignUpScreen = ({ navigation }) => {
  // ... (Votre gestion de state et fonctions existantes)

  const [fontsLoaded, setFontsLoaded] = useState(false);

  useEffect(() => {
    async function loadFonts() {
      await Font.loadAsync({
        'Rowdies': require('./assets/fonts/Rowdies-Regular.ttf'), // Chemin vers votre fichier de police
      });
      setFontsLoaded(true);
    }
    loadFonts();
  }, []);

  if (!fontsLoaded) {
    return null;
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Inscription</Text>
      <Image 
        source={require('../assets/logo.png')} // Ou le chemin vers votre logo 
        style={styles.logo} 
      />

      {/* Champs de formulaire */}
      {[
        { label: 'NOM', value: name, onChangeText: setName },
        { label: 'PRÉNOM', value: '', onChangeText: () => {} }, // Exemple de champ vide
        { label: 'PSEUDO', value: '', onChangeText: () => {} },
        { label: 'VILLE', value: city, onChangeText: setCity },
        { label: 'EMAIL', value: email, onChangeText: setEmail, keyboardType: 'email-address', autoCapitalize: 'none' },
        { label: 'MOT DE PASSE', value: password, onChangeText: setPassword, secureTextEntry: true },
      ].map(({ label, value, onChangeText, ...rest }, index) => (
        <View key={index} style={styles.inputContainer}>
          <Text style={styles.label}>{label}</Text>
          <TextInput
            style={styles.input}
            value={value}
            onChangeText={onChangeText}
            {...rest} // Passer les autres props (keyboardType, etc.)
          />
        </View>
      ))}

      <Pressable style={styles.photoButton} onPress={handleChoosePhoto}>
        <Text style={styles.photoButtonText}>PHOTO DE PROFIL</Text>
      </Pressable>
      {profileImage && (
        <Image
          source={profileImage}
          style={styles.profileImage}
        />
      )}

      <Pressable style={styles.signupButton} onPress={handleSignUp}>
        <Text style={styles.signupButtonText}>S'enregistrer</Text>
      </Pressable>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#A0AE88', // Couleur de fond de l'image
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20,
  },
  title: {
    fontFamily: 'Rowdies',
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 20,
  },
  logo: {
    width: 100,
    height: 100,
    borderRadius: 50,
    marginBottom: 30,
  },
  inputContainer: {
    width: '100%',
    marginBottom: 15, // Espacement entre les champs
  },
  label: {
    fontFamily: 'Rowdies',
    fontSize: 16,
    color: 'white',
    marginBottom: 5,
  },
  input: {
    backgroundColor: '#D9D9D9', // Couleur de fond des champs
    height: 40,
    borderRadius: 10,
    paddingHorizontal: 10,
    fontFamily: 'Rowdies', // Appliquer la police Rowdies aux inputs
  },
  photoButton: {
    backgroundColor: '#D9D9D9',
    padding: 10,
    borderRadius: 10,
    marginBottom: 10,
  },
  photoButtonText: {
    fontFamily: 'Rowdies',
    color: '#6E745C',
    fontSize: 16,
  },
  profileImage: {
    width: 100,
    height: 100,
    borderRadius: 50,
    marginBottom: 20,
  },
  signupButton: {
    backgroundColor: '#D9D9D9',
    padding: 15,
    borderRadius: 10,
    marginTop: 20,
  },
  signupButtonText: {
    fontFamily: 'Rowdies',
    color: '#6E745C',
    fontSize: 18,
    fontWeight: 'bold',
  },
});
