import React, { useEffect, useState } from 'react';
import { View, Text, Image, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import axios from 'axios';
import { globalStyles } from '../Style/globalFont';
import { Ionicons } from '@expo/vector-icons'; 

export function ProfileScreen({ route, navigation }) {
  // ... (votre logique de récupération des données de profil et d'articles)

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.title}>Mon Profil</Text>
        <View style={styles.icons}>
          <TouchableOpacity>
            <Ionicons name="settings-outline" size={24} color="black" />
          </TouchableOpacity>
          <TouchableOpacity>
            <Ionicons name="information-circle-outline" size={24} color="black" />
          </TouchableOpacity>
        </View>
      </View>

      <ScrollView contentContainerStyle={styles.scrollContent}>
        {/* Informations du profil */}

      </ScrollView>

      {/* Barre de navigation inférieure */}
      <View style={styles.bottomNavBar}>
      <Pressable style={styles.iconButton} onPress={() => navigation.navigate('Welcome')}>
          <Ionicons name="home-outline" size={24} color="black" />
        </Pressable>
        <Pressable style={styles.iconButton} onPress={() => navigation.navigate('ProfileScreen', { name, city, email })}>
          <Ionicons name="person-outline" size={24} color="black" />
        </Pressable>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#A0AE88', // Couleur de fond principale
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingTop: 50, // Marge supérieure plus importante
    paddingBottom: 20,
    borderBottomWidth: 1, // Ligne de séparation
    borderBottomColor: '#ccc', // Couleur de la ligne de séparation
  },
  title: {
    fontFamily: 'Rowdies',
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
  },
  icons: {
    flexDirection: 'row',
  },
  scrollContent: {
    flexGrow: 1, // Permet au contenu de remplir l'espace disponible
    alignItems: 'center',
    paddingBottom: 80, // Espace pour la barre de navigation inférieure
  },
  profileInfo: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 20,
    borderRadius: 10,
    marginBottom: 20,
    width: '90%', // Largeur du conteneur d'informations du profil
  },
  profilePic: {
    width: 80,
    height: 80,
    borderRadius: 40,
    marginRight: 20,
  },
  profileDetails: {
    flex: 1, // Permet aux détails de prendre l'espace restant
  },
  name: {
    fontFamily: 'Rowdies',
    fontSize: 18,
    fontWeight: 'bold',
  },
  city: {
    fontFamily: 'Rowdies',
    fontSize: 16,
  },
  stats: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 10,
  },
  statItem: {
    alignItems: 'center',
  },
  statNumber: {
    fontFamily: 'Rowdies',
    fontSize: 16,
    fontWeight: 'bold',
  },
  statLabel: {
    fontFamily: 'Rowdies',
    fontSize: 14,
  },
  articlesGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    width: '90%', // Largeur de la grille d'articles
  },
  articleCard: {
    backgroundColor: '#C9AC96', 
    width: '31%', // Largeur d'une carte (3 colonnes avec une petite marge)
    aspectRatio: 1, // Maintient le ratio 1:1 (carré)
    margin: 5,
    borderRadius: 10,
  },
  bottomNavBar: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    backgroundColor: '#D9D9D9',
    width: '100%',
    padding: 15,
    position: 'absolute',
    bottom: 0,
  },
});
